    public void print( String message ) throws CCMException
    {
        try
        {
            wamas.io.OutputStream os = component.ctx.get_connection_stream();
            for (int i = 0; i < message.length(); ++i)
            {
                os.write(message.charAt(i));
            }
            os.write('\n');
        }
        catch (Exception e)
        {
            throw new CCMException(e.getMessage(), CCMExceptionReason.SYSTEM_ERROR);
        }
    }    

