# $Id$

PROVIDE_SYMBOL('BOOST_LAMBDA')
REQUIRE_SYMBOL('BOOST_ENTRY', REQUIRED)
REQUIRE_SYMBOL('BOOST_COMMON', REQUIRED)

PROVIDE_H('boost/lambda/*')

ACINCLUDE_M4(
    lines=['AC_DEFUN([CONFIX_BOOST_LAMBDA], [AC_REQUIRE([CONFIX_BOOST])])'],
    propagate_only=1)

CONFIGURE_IN(
    lines=['CONFIX_BOOST_LAMBDA'],
    order=AC_LIBRARIES,
    propagate_only=1)

EXTERNAL_LIBRARY2(inc='@BOOST_INC@')
